  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/node_modules/.pnpm/sanity@3.99.0_@emotion+is-p_2dc8747c86d0a70e06ade18ef91d6a92/node_modules/sanity/bin/node_modules:/mnt/d/node_modules/.pnpm/sanity@3.99.0_@emotion+is-p_2dc8747c86d0a70e06ade18ef91d6a92/node_modules/sanity/node_modules:/mnt/d/node_modules/.pnpm/sanity@3.99.0_@emotion+is-p_2dc8747c86d0a70e06ade18ef91d6a92/node_modules:/mnt/d/node_modules/.pnpm/node_modules"
